var log4js=require('log4js')
log4js.configure({
    appenders: [
        { type: 'console' },
        { type: 'dateFile', filename: 'test.log', "pattern": "-yyyy-MM-dd","alwaysIncludePattern": true,category: 'test' },
    ]
})

function getLogger(log) {
    return log4js.getLogger(log)
}

module.exports=getLogger


